import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from "react";
import Axios from "axios"

function App() {
  const [clientInfo, setClient] = useState([]);
  const [acc_no, setAcc_no] = useState("");
  const [age, setAge] = useState("");
  const [c_first, setFirst] = useState("");
  const [c_last, setLast] = useState("");
  const [phone, setPhone] = useState("");

  const handleSubmit = (e) => {
    try {
      Axios.post("https://proj174.herokuapp.com/insert", {
      clientInfo: clientInfo,
      acc_no :acc_no,
      age : age,
      c_first : c_first,
      c_last : c_last,
      phone : phone
      });
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect (() => {
    Axios.get("https://proj174.herokuapp.com/select").then(
      (response) => {
        setClient(response.data.rows);
        console.log(response.data.rows);
      }
    );
  }, []);

  return (
    <div className="App">
      <h1>Appointment Booking Service</h1>
      <p>Enter your personal info</p>
      <form onSubmit ={handleSubmit}>
      <label>
        Account Number:
        <input
        onChange ={(e) => setAcc_no(e.target.value)}
        value = {acc_no}
        ></input>
      </label>
      <br />
      <label>
        Age:
        <input
        onChange ={(e) => setAge(e.target.value)}
        value = {age}
        ></input>
      </label>
      <br />
      <label>
        First Name:
        <input
        onChange ={(e) => setFirst(e.target.value)}
        value = {c_first}
        ></input>
      </label>
      <br />
      <label>
        Last Name:
        <input
        onChange ={(e) => setLast(e.target.value)}
        value = {c_last}
        ></input>
      </label>
      <br />
      <label>
        Phone Number:
        <input
        onChange ={(e) => setPhone(e.target.value)}
        value = {phone}
        ></input>
      </label>
      <br />
      <button type = "Confirm">Confirm</button>
      </form>

    <table>
      <thread>
        <tr>
          <th>Phone</th>
          <th>LastName</th>
          <th>FirstName</th>
          <th>Age</th>
          <th>AccountNumber</th>
        </tr>
      </thread>
      <tbody>
        {clientInfo.map((val,key) =>(
          <tr key ={key}>
            <td>{val.Phone}</td>
            <td>{val.LastName}</td>
            <td>{val.FirstName}</td>
            <td>{val.Age}</td>
            <td>{val.AccountNumber}</td>
          </tr>
        ))}
      </tbody>
    </table>
     </div>
  );
}

export default App;
